$ErrorActionPreference='Stop'
Write-Host 'Ensuring native Docker Engine inside Ubuntu-Dev...' -ForegroundColor Cyan
& wsl --% -d Ubuntu-Dev bash -lc "set -e; export DEBIAN_FRONTEND=noninteractive; arch=$(dpkg --print-architecture); codename=$( . /etc/os-release; echo $UBUNTU_CODENAME ); sudo install -m 0755 -d /etc/apt/keyrings; if [ ! -f /etc/apt/keyrings/docker.gpg ]; then curl -fsSL https://download.docker.com/linux/ubuntu/gpg | sudo gpg --dearmor -o /etc/apt/keyrings/docker.gpg; sudo chmod a+r /etc/apt/keyrings/docker.gpg; fi; echo \"deb [arch=\$arch signed-by=/etc/apt/keyrings/docker.gpg] https://download.docker.com/linux/ubuntu \$codename stable\" | sudo tee /etc/apt/sources.list.d/docker.list > /dev/null; sudo apt-get update -y; sudo apt-get install -y docker-ce docker-ce-cli containerd.io docker-buildx-plugin docker-compose-plugin; sudo usermod -aG docker $USER || true; sudo systemctl enable docker || true; sudo systemctl restart docker || true; docker info --format '{{.ServerVersion}}' || exit 5" | Out-Null
if ($LASTEXITCODE -ne 0) { Write-Error "Docker install/verify failed with code $LASTEXITCODE"; exit 1 }
Write-Host 'Docker Engine present.' -ForegroundColor Green
